         &lt;li&gt;
         	&lt;p&gt;
               &lt;span&gt;立即执行函数&lt;/span&gt;
            &lt;/p&gt;
         &lt;/li&gt;
         &lt;li&gt;
         	&lt;p&gt;
               &lt;span&gt;数组解构&lt;/span&gt;
            &lt;/p&gt;
