&lt;p&gt;&lt;img src=&#34;https://raw.githubusercontent.com/PigPigLetsGo/imeages/master/202310061440364.png&#34; alt=&#34;image-20210410103322874&#34;&gt;&lt;/p&gt;
&lt;p&gt;修改 conf/nginx.conf 文件，配置如下：&lt;/p&gt;
&lt;figure class=&#34;highlight nginx&#34;&gt;&lt;figcaption data-lang=&#34;nginx&#34;&gt;&lt;/figcaption&gt;&lt;table&gt;&lt;tr&gt;&lt;td data-num=&#34;1&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;span class=&#34;token directive&#34;&gt;&lt;span class=&#34;token keyword&#34;&gt;http&lt;/span&gt;&lt;/span&gt; &lt;span class=&#34;token punctuation&#34;&gt;&amp;#123;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;2&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;   &lt;span class=&#34;token directive&#34;&gt;&lt;span class=&#34;token keyword&#34;&gt;upstream&lt;/span&gt; nacos-cluster&lt;/span&gt; &lt;span class=&#34;token punctuation&#34;&gt;&amp;#123;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;3&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;    	&lt;span class=&#34;token directive&#34;&gt;&lt;span class=&#34;token keyword&#34;&gt;server&lt;/span&gt; 127.0.0.1:8845&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;4&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;		&lt;span class=&#34;token directive&#34;&gt;&lt;span class=&#34;token keyword&#34;&gt;server&lt;/span&gt; 127.0.0.1:8846&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;5&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;		&lt;span class=&#34;token directive&#34;&gt;&lt;span class=&#34;token keyword&#34;&gt;server&lt;/span&gt; 127.0.0.1:8847&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;6&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;	&lt;span class=&#34;token punctuation&#34;&gt;&amp;#125;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;7&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;8&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;	&lt;span class=&#34;token directive&#34;&gt;&lt;span class=&#34;token keyword&#34;&gt;server&lt;/span&gt;&lt;/span&gt; &lt;span class=&#34;token punctuation&#34;&gt;&amp;#123;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;9&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;    	&lt;span class=&#34;token directive&#34;&gt;&lt;span class=&#34;token keyword&#34;&gt;listen&lt;/span&gt;       &lt;span class=&#34;token number&#34;&gt;80&lt;/span&gt;&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;10&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;    	&lt;span class=&#34;token directive&#34;&gt;&lt;span class=&#34;token keyword&#34;&gt;server_name&lt;/span&gt;  localhost&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;11&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;12&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;    	&lt;span class=&#34;token directive&#34;&gt;&lt;span class=&#34;token keyword&#34;&gt;location&lt;/span&gt; /nacos&lt;/span&gt; &lt;span class=&#34;token punctuation&#34;&gt;&amp;#123;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;13&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;        &lt;span class=&#34;token directive&#34;&gt;&lt;span class=&#34;token keyword&#34;&gt;proxy_pass&lt;/span&gt; http://nacos-cluster&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;14&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;    	&lt;span class=&#34;token punctuation&#34;&gt;&amp;#125;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;15&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;	&lt;span class=&#34;token punctuation&#34;&gt;&amp;#125;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;16&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;span class=&#34;token punctuation&#34;&gt;&amp;#125;&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;p&gt;完整的配置文件信息：&lt;/p&gt;
&lt;pre&gt;&lt;code&gt;
#user  nobody;
worker_processes  1;

#error_log  logs/error.log;
#error_log  logs/error.log  notice;
#error_log  logs/error.log  info;

#pid        logs/nginx.pid;


events &amp;#123;
    worker_connections  1024;
&amp;#125;


http &amp;#123;
    include       mime.types;
    default_type  application/octet-stream;

    #log_format  main  &#39;$remote_addr - $remote_user [$time_local] &amp;quot;$request&amp;quot; &#39;
    #                  &#39;$status $body_bytes_sent &amp;quot;$http_referer&amp;quot; &#39;
    #                  &#39;&amp;quot;$http_user_agent&amp;quot; &amp;quot;$http_x_forwarded_for&amp;quot;&#39;;

    #access_log  logs/access.log  main;

    sendfile        on;
    #tcp_nopush     on;

    #keepalive_timeout  0;
    keepalive_timeout  65;

    #gzip  on;

    server &amp;#123;
        listen       88;
        server_name  localhost;

        location / &amp;#123;
            root   html;
            index  index.html index.htm;
        &amp;#125;
        error_page   500 502 503 504  /50x.html;
        location = /50x.html &amp;#123;
            root   html;
        &amp;#125;
    &amp;#125;
	upstream nacos-cluster &amp;#123;
    		server 127.0.0.1:8845;
		server 127.0.0.1:8846;
		server 127.0.0.1:8847;
	&amp;#125;

	server &amp;#123;
    		listen       80;
    		server_name  localhost;

    		location /nacos &amp;#123;
        		proxy_pass http://nacos-cluster;
    		&amp;#125;
	&amp;#125;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;配置完成后启动 nginx，执行命令：start nginx.exe&lt;/p&gt;
&lt;p&gt;访问：&lt;a href=&#34;http://localhost:88/%E6%9F%A5%E7%9C%8B%E6%98%AF%E5%90%A6%E8%83%BD%E6%AD%A3%E5%B8%B8%E8%AE%BF%E9%97%AE&#34;&gt;http://localhost:88 / 查看是否能正常访问&lt;/a&gt;&lt;/p&gt;
&lt;p&gt;nginx 启动成功！&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;https://raw.githubusercontent.com/PigPigLetsGo/imeages/master/202310061521543.png&#34; alt=&#34;image-20231006152139209&#34;&gt;&lt;/p&gt;
&lt;p&gt;而后在浏览器访问：&lt;a href=&#34;http://localhost/nacos%E5%8D%B3%E5%8F%AF%E3%80%82&#34;&gt;http://localhost/nacos 即可。&lt;/a&gt;&lt;/p&gt;
&lt;p&gt;到这儿！集群搭建就成功了。看着像是访问的一个 nacos，事实上它会在三个 nacos 之间做一个负载均衡&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;https://raw.githubusercontent.com/PigPigLetsGo/imeages/master/202310061522455.png&#34; alt=&#34;image-20231006152205310&#34;&gt;&lt;/p&gt;
&lt;p&gt;问题来了，java 代码该如何配呢，在 java 的 yml 配置文件中将 nacos 的服务地址改为对应的端口也就是 80 端口&lt;/p&gt;
&lt;p&gt;代码中 bootstrap.yml 文件配置如下：&lt;/p&gt;
&lt;figure class=&#34;highlight yaml&#34;&gt;&lt;figcaption data-lang=&#34;YAML&#34;&gt;&lt;/figcaption&gt;&lt;table&gt;&lt;tr&gt;&lt;td data-num=&#34;1&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;span class=&#34;token key atrule&#34;&gt;spring&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;:&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;2&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;  &lt;span class=&#34;token key atrule&#34;&gt;application&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;:&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;3&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;    &lt;span class=&#34;token key atrule&#34;&gt;name&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;:&lt;/span&gt; userservice &lt;span class=&#34;token comment&#34;&gt;# 服务名称&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;4&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;  &lt;span class=&#34;token key atrule&#34;&gt;profiles&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;:&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;5&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;    &lt;span class=&#34;token key atrule&#34;&gt;active&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;:&lt;/span&gt; dev &lt;span class=&#34;token comment&#34;&gt;# 环境&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;6&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;  &lt;span class=&#34;token key atrule&#34;&gt;cloud&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;:&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;7&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;    &lt;span class=&#34;token key atrule&#34;&gt;nacos&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;:&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;8&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;      &lt;span class=&#34;token key atrule&#34;&gt;server-addr&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;:&lt;/span&gt; localhost&lt;span class=&#34;token punctuation&#34;&gt;:&lt;/span&gt;&lt;span class=&#34;token number&#34;&gt;80&lt;/span&gt; &lt;span class=&#34;token comment&#34;&gt;# nacos 地址，这里找的是 nginx 反向代理的地址它会根据负载均衡来启动 nacos&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;9&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;      &lt;span class=&#34;token key atrule&#34;&gt;config&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;:&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;10&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;        &lt;span class=&#34;token key atrule&#34;&gt;file-extension&lt;/span&gt;&lt;span class=&#34;token punctuation&#34;&gt;:&lt;/span&gt; yaml &lt;span class=&#34;token comment&#34;&gt;# 配置文件后缀名&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;tr&gt;&lt;td data-num=&#34;11&#34;&gt;&lt;/td&gt;&lt;td&gt;&lt;pre&gt;&lt;span class=&#34;token comment&#34;&gt;# 三要素：服务名称，环境，后缀名 而这三个决定了 DateId&lt;/span&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;&lt;p&gt;启动 user-service 服务&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;https://raw.githubusercontent.com/PigPigLetsGo/imeages/master/202310061528874.png&#34; alt=&#34;image-20231006152811433&#34;&gt;&lt;/p&gt;
&lt;p&gt;我们查看 nacos 控制台中的服务列表。&lt;/p&gt;
&lt;p&gt;可以看到成功的注册进来了。&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;https://raw.githubusercontent.com/PigPigLetsGo/imeages/master/202310061528168.png&#34; alt=&#34;image-20231006152847028&#34;&gt;&lt;/p&gt;
&lt;p&gt;我们创建一个配置文件，点击发布&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;https://raw.githubusercontent.com/PigPigLetsGo/imeages/master/202310061544827.png&#34; alt=&#34;image-20231006154304156&#34;&gt;&lt;/p&gt;
&lt;p&gt;发布后查看数据库中的 config_info 表里面的信息，就可以看到多了一条信息就是创建的环境配置文件的信息&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;https://raw.githubusercontent.com/PigPigLetsGo/imeages/master/202310061545446.png&#34; alt=&#34;image-20231006154339296&#34;&gt;&lt;/p&gt;
&lt;blockquote&gt;
&lt;p&gt;&lt;strong&gt;总结&lt;/strong&gt;：&lt;/p&gt;
&lt;p&gt;集群搭建步骤：&lt;/p&gt;
&lt;ol&gt;
&lt;li&gt;搭建 MySQL 集群并初始化数据库表&lt;/li&gt;
&lt;li&gt;下载解压 nacos&lt;/li&gt;
&lt;li&gt;修改集群配置 (节点信息)，数据库配置&lt;/li&gt;
&lt;li&gt;分别启动多个 nacos 节点&lt;/li&gt;
&lt;li&gt;nginx 反向代理&lt;/li&gt;
&lt;/ol&gt;
&lt;/blockquote&gt;
&lt;h2 id=&#34;26优化&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#26优化&#34;&gt;#&lt;/a&gt; 2.6. 优化&lt;/h2&gt;
&lt;ul&gt;
&lt;li&gt;
&lt;p&gt;实际部署时，需要给做反向代理的 nginx 服务器设置一个域名，这样后续如果有服务器迁移 nacos 的客户端也无需更改配置.&lt;/p&gt;
&lt;/li&gt;
&lt;li&gt;
&lt;p&gt;Nacos 的各个节点应该部署到多个不同服务器，做好容灾和隔离&lt;/p&gt;
&lt;/li&gt;
&lt;/ul&gt;

 &lt;h1 id=&#34;nacos安装指南&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#nacos安装指南&#34;&gt;#&lt;/a&gt; Nacos 安装指南&lt;/h1&gt;
&lt;h1 id=&#34;1windows安装&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#1windows安装&#34;&gt;#&lt;/a&gt; 1.Windows 安装&lt;/h1&gt;
&lt;p&gt;开发阶段采用单机安装即可。&lt;/p&gt;
&lt;h2 id=&#34;11下载安装包&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#11下载安装包&#34;&gt;#&lt;/a&gt; 1.1. 下载安装包&lt;/h2&gt;
&lt;p&gt;在 Nacos 的 GitHub 页面，提供有下载链接，可以下载编译好的 Nacos 服务端或者源代码：&lt;/p&gt;
&lt;p&gt;GitHub 主页：&lt;a href=&#34;https://github.com/alibaba/nacos&#34;&gt;https://github.com/alibaba/nacos&lt;/a&gt;&lt;/p&gt;
&lt;p&gt;GitHub 的 Release 下载页：&lt;a href=&#34;https://github.com/alibaba/nacos/releases&#34;&gt;https://github.com/alibaba/nacos/releases&lt;/a&gt;&lt;/p&gt;
&lt;p&gt;如图：&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;https://raw.githubusercontent.com/PigPigLetsGo/imeages/master/202310051218392.png&#34; alt=&#34;image-20210402161102887&#34;&gt;&lt;/p&gt;
&lt;p&gt;本课程采用 1.4.1. 版本的 Nacos，课前资料已经准备了安装包：&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;https://raw.githubusercontent.com/PigPigLetsGo/imeages/master/202310051218840.png&#34; alt=&#34;image-20210402161130261&#34;&gt;&lt;/p&gt;
&lt;p&gt;windows 版本使用 &lt;code&gt;nacos-server-1.4.1.zip&lt;/code&gt;  包即可。&lt;/p&gt;
&lt;h2 id=&#34;12解压&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#12解压&#34;&gt;#&lt;/a&gt; 1.2. 解压&lt;/h2&gt;
&lt;p&gt;将这个包解压到任意非中文目录下，如图：&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;https://raw.githubusercontent.com/PigPigLetsGo/imeages/master/202310051218669.png&#34; alt=&#34;image-20210402161843337&#34;&gt;&lt;/p&gt;
&lt;p&gt;目录说明：&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;bin：启动脚本&lt;/li&gt;
&lt;li&gt;conf：配置文件&lt;/li&gt;
&lt;/ul&gt;
&lt;h2 id=&#34;13端口配置&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#13端口配置&#34;&gt;#&lt;/a&gt; 1.3. 端口配置&lt;/h2&gt;
&lt;p&gt;Nacos 的默认端口是 8848，如果你电脑上的其它进程占用了 8848 端口，请先尝试关闭该进程。&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;如果无法关闭占用 8848 端口的进程&lt;/strong&gt;，也可以进入 nacos 的 conf 目录，修改配置文件中的端口：&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;https://raw.githubusercontent.com/PigPigLetsGo/imeages/master/202310051218946.png&#34; alt=&#34;image-20210402162008280&#34;&gt;&lt;/p&gt;
&lt;p&gt;修改其中的内容：&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;https://raw.githubusercontent.com/PigPigLetsGo/imeages/master/202310051218758.png&#34; alt=&#34;image-20210402162251093&#34;&gt;&lt;/p&gt;
&lt;h2 id=&#34;14启动&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#14启动&#34;&gt;#&lt;/a&gt; 1.4. 启动&lt;/h2&gt;
&lt;p&gt;启动非常简单，进入 bin 目录，结构如下：&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;https://raw.githubusercontent.com/PigPigLetsGo/imeages/master/202310051218561.png&#34; alt=&#34;image-20210402162350977&#34;&gt;&lt;/p&gt;
&lt;p&gt;然后执行命令即可：&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;
&lt;p&gt;windows 命令：&lt;/p&gt;
&lt;pre&gt;&lt;code&gt;startup.cmd -m standalone
&lt;/code&gt;&lt;/pre&gt;
&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;执行后的效果如图：&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;https://raw.githubusercontent.com/PigPigLetsGo/imeages/master/202310051218179.png&#34; alt=&#34;image-20210402162526774&#34;&gt;&lt;/p&gt;
&lt;h2 id=&#34;15访问&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#15访问&#34;&gt;#&lt;/a&gt; 1.5. 访问&lt;/h2&gt;
&lt;p&gt;在浏览器输入地址：&lt;a href=&#34;http://127.0.0.1:8848/nacos%E5%8D%B3%E5%8F%AF%EF%BC%9A&#34;&gt;http://127.0.0.1:8848/nacos 即可：&lt;/a&gt;&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;https://raw.githubusercontent.com/PigPigLetsGo/imeages/master/202310051218689.png&#34; alt=&#34;image-20210402162630427&#34;&gt;&lt;/p&gt;
&lt;p&gt;默认的账号和密码都是 nacos，进入后：&lt;/p&gt;
&lt;p&gt;&lt;img src=&#34;https://raw.githubusercontent.com/PigPigLetsGo/imeages/master/202310051218382.png&#34; alt=&#34;image-20210402162709515&#34;&gt;&lt;/p&gt;
&lt;h1 id=&#34;2linux安装&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#2linux安装&#34;&gt;#&lt;/a&gt; 2.Linux 安装&lt;/h1&gt;
&lt;p&gt;Linux 或者 Mac 安装方式与 Windows 类似。&lt;/p&gt;
&lt;h2 id=&#34;21安装jdk&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#21安装jdk&#34;&gt;#&lt;/a&gt; 2.1. 安装 JDK&lt;/h2&gt;
&lt;p&gt;Nacos 依赖于 JDK 运行，所以 Linux 上也需要安装 JDK 才行。&lt;/p&gt;
&lt;p&gt;上传 jdk 安装包：&lt;/p&gt;
