        location /img &amp;#123;
            #root 只能查找本机中根目录的项目
            #不需要加额外的目录,在访问时会指向根路径然后顺便加上uri的地址同时拼接                                                                         
            root   html;
            index  index.html index.htm;
        &amp;#125;
