&lt;pre&gt;&lt;code&gt;org.springframework.boot.autoconfigure.EnableAutoConfiguration=\
	com.heima.common.aliyun.GreenImageScan,\
  com.heima.common.aliyun.GreenTextScan
&lt;/code&gt;&lt;/pre&gt;
